  PROJECT_ID = #{projectId,jdbcType=BIGINT},
  USER_PROJECT_TYPE = #{userProjectType,jdbcType=VARCHAR},
  PROJECT_USER_GROUP_ID = #{projectUserGroupId,jdbcType=BIGINT}
where USER_PROJECT_ID = #{userProjectId,jdbcType=BIGINT}
